import React from 'react'
import { useState } from 'react'
import './Card.css'
import more from './more.png'
import { Link } from 'react-router-dom'


const Card = (props) =>  {

  const [count, setCount] = useState(0)
  const updateCount = () => {
    setCount((count) => count + 1);
  }

  return (
      <div className="Card">
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          {/* <h2 className="title">{props.title}</h2>
          <h3 className="author">{"by " + props.author}</h3>
          <p className="description">{props.description}</p>
          <p className="created_at">{"Posted: " + props.created_at}</p>
          

          <button className="upvote" onClick={updateCount} >Upvotes: {count}</button> */}

          <Link to={'view/'+ props.id} style={{ textDecoration: 'none', color: 'inherit' }}>
              <h2 className="title">{props.title}</h2>
              <h3 className="author">{"by " + props.author}</h3>
              <p className="description">{props.description}</p>
              <p className="created_at">{props.created_at}</p>
              <button className="upvote" onClick={updateCount} >Upvotes: {count}</button>
          </Link>
          
      </div>
  );
};

export default Card;
