// PostView.js

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client.js';

const PostView = () => {
     const { id } = useParams();
     //const [post, setPost] = useState(null);
     const [loading, setLoading] = useState(true);
     const [count, setCount] = useState(0);
    // const [comments, setComments] = useState([]); // new state for comments
    // const [newComment, setNewComment] = useState(''); // new state for the new comment
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [newCommentAuthor, setNewCommentAuthor] = useState(''); // new state for the new comment author

    useEffect(() => {
        console.log('useEffect is being executed');
        fetchPost();
        fetchComments();
    }, [id]);
    
    const fetchPost = async () => {
        const { data, error } = await supabase
            .from('Posts')
            .select('title, author, description, created_at, upvote')
            .eq('id', id);
        console.log('data:', data);
        console.log('error:', error);
        if (error) {
            console.error('Error fetching post: ', error);
        } else {
            console.log('Setting post and loading state');
            setPost(data[0]);
            setCount(data[0].upvote);
            setLoading(false);
        }
    };

    const fetchComments = async () => {
        const { data } = await supabase
            .from('Comments')
            .select('*')
            .eq('post_id', id)
            .order('created_at', { ascending: false });

        setComments(data);
    }

    const handleNewCommentChange = (e) => {
        setNewComment(e.target.value);
    }

    const handleNewCommentSubmit = async (e) => {
        e.preventDefault();

        const { error } = await supabase
            .from('Comments')
            .insert([{ post_id: id, content: newComment, author: 'Your Name', created_at: new Date() }]);

        if (error) {
            console.log('error', error);
        } else {
            setNewComment('');
            fetchComments(); // fetch the updated comments after adding a new comment
        }
    }

    // const handleUpvote = async () => {
    //     const { data, error } = await supabase
    //         .from('Posts')
    //         .update({ upvote: post.upvote + 1 })
    //         .eq('id', post.id);
    
    //     if (error) {
    //         console.log('error', error);
    //     } else {
    //         setPost({ ...post, upvote: post.upvote + 1 });
    //     }
    // };

    const handleUpvote = async () => {
        const { error } = await supabase
            .from('Posts')
            .update({ upvote: post.upvote + 1 })
            .eq('id', post.id);
    
        if (error) {
            console.log('error', error);
        } else {
            const { data, error } = await supabase
                .from('Posts')
                .select('*')
                .eq('id', post.id)
                .single();
    
            if (error) {
                console.log('error', error);
            } else {
                setPost(data); // update the post state with the updated post
            }
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    // return (
    //     <div>
    //         {post && (
    //             <>
    //                 <h2>{post.title}</h2>
    //                 <h3>{"by " + post.author}</h3>
    //                 <p>{post.description}</p>
    //                 {/* <p>{"Posted: " + post.created_at}</p> */}
    //                 <p>{"Posted: " + new Date(post.created_at).toLocaleString()}</p>
    //                 <button onClick={handleUpvote}>Upvote</button>
    //                 <p>{"Upvotes: " + post.upvote}</p>
                    
    //             </>
                
    //         )}
    //     </div>
    // );

    return (
        <div>
            {post && (
                <>
                    <h2>{post.title}</h2>
                    <h3>{"by " + post.author}</h3>
                    <p>{post.description}</p>
                    <p>{"Posted: " + new Date(post.created_at).toLocaleString()}</p>
                    <button onClick={handleUpvote}>Upvote</button>
                    <p>{"Upvotes: " + post.upvote}</p>
    
                    <form onSubmit={handleNewCommentSubmit}>
                        <input type="text" value={newComment} onChange={handleNewCommentChange} placeholder="New comment" required />
                        <button type="submit">Submit</button>
                    </form>
    
                    {comments.map((comment) => (
                        <div key={comment.id}>
                            <p>{comment.content}</p>
                            <p>{"by " + comment.author}</p>
                            <p>{"Posted: " + new Date(comment.created_at).toLocaleString()}</p>
                        </div>
                    ))}
                </>
            )}
        </div>
    );

};

export default PostView;
