import React from 'react'
import { useState, useEffect } from 'react'
import './Card.css'
import more from './more.png'
import { Link } from 'react-router-dom'
import { supabase } from '../client.js';

const Card = (props) =>  {

  const [count, setCount] = useState(0)
  // const updateCount = () => {
  //   setCount((count) => count + 1);
  // }

  useEffect(() => {
    fetchCount();
}, []);

const fetchCount = async () => {
    const { data, error } = await supabase
        .from('your_table_name') // replace 'your_table_name' with the name of your table
        .select('upvotes') // replace 'upvotes' with the name of your upvote column
        .eq('id', props.id);
    if (error) {
        console.error('Error fetching upvote count: ', error);
    } else {
        setCount(data[0].upvotes);
    }
};

//   const updateCount = async () => {
//     const { data, error } = await supabase
//         .from('Posts') // replace 'your_table_name' with the name of your table
//         .update({ upvote: count + 1 }) // replace 'upvotes' with the name of your upvote column
//         .eq('id', props.id);
//     if (error) {
//         console.error('Error updating upvote count: ', error);
//     } else {
//         setCount(count + 1);
//     }
// };

const updateCount = async () => {
  // Fetch the current upvote count from the database
  const { data: fetchData, error: fetchError } = await supabase
      .from('Posts') // replace 'Posts' with the name of your table
      .select('upvote') // replace 'upvote' with the name of your upvote column
      .eq('id', props.id);
  if (fetchError) {
      console.error('Error fetching upvote count: ', fetchError);
      return;
  }

  // Increment the upvote count and update it in the database
  const newCount = fetchData[0].upvote + 1;
  const { data: updateData, error: updateError } = await supabase
      .from('Posts') // replace 'Posts' with the name of your table
      .update({ upvote: newCount }) // update the upvote count in the database
      .eq('id', props.id);
  if (updateError) {
      console.error('Error updating upvote count: ', updateError);
  } else {
      setCount(newCount); // update the local state
  }
};

  return (
      <div className="Card">
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          {/* <h2 className="title">{props.title}</h2>
          <h3 className="author">{"by " + props.author}</h3>
          <p className="description">{props.description}</p>
          <p className="created_at">{"Posted: " + props.created_at}</p>
          

          <button className="upvote" onClick={updateCount} >Upvotes: {count}</button> */}

          <Link to={'view/'+ props.id} style={{ textDecoration: 'none', color: 'inherit' }}>
              <h2 className="title">{props.title}</h2>
              <h3 className="author">{"by " + props.author}</h3>
              <p className="description">{props.description}</p>
              {/* <p className="created_at">{props.created_at}</p> */}
              <p className="created_at">{"Posted: " + new Date(props.created_at).toLocaleString()}</p>
              
          </Link>
          <p className="upvotes">{"Upvotes: " + props.upvote}</p>
          <button className="upvote" onClick={updateCount} >Upvote</button>
          
      </div>
  );
};

export default Card;
